INSERT INTO `tips` (`idTips`, `TieuDe`, `AnhDaiDien`, `NoiDungTomTat`, `NoiDung`, `NgayDang`, `NguoiDang`, `LoaiBai`, `Xoa`) VALUES
(2, 'Cẩm Nang Cho Người Mới Bắt Đầu', 'http://static1.squarespace.com/static/5922f26a8419c22a1e72d402/5b51f4e96d2a735aec402ee7/5be08281032be49b18b8ac98/1548954754826/IMAGINE.jpg?format=1500w', 'Bạn là 1 người mới làm quen với tiếng Anh? Đừng quá lo lắng hãy đọc bài viết này để xác định cho mình 1 con đường học tiếng Anh phù hợp nhé.', '<p>C&aacute;c bạn đang trong thắc mắc về c&aacute;ch<a href=\"https://jaxtina.com/khoa-hoc/khoa-hoc-tieng-anh-cho-nguoi-moi-bat-dau/\"><strong>&nbsp;học tiếng anh cho người mới bắt đầu</strong></a><strong>&nbsp;</strong>hoặc mất gốc sau thời gian d&agrave;i kh&ocirc;ng tiếp x&uacute;c, v&agrave; bạn đang suy nghĩ trong đầu những băn khoăn :</p>\r\n<ul>\r\n<li>Tự ti v&igrave; mất hết lượng kiến thức căn bản</li>\r\n<li>Lo lắng v&igrave; m&igrave;nh bắt đầu h&agrave;nh tr&igrave;nh học tiếng Anh chậm hơn người kh&aacute;c</li>\r\n<li>Nghĩ rằng tiếng Anh kh&oacute; học, m&igrave;nh sẽ rất kh&oacute; khăn để theo kịp người ta</li>\r\n<li>Sợ h&atilde;i v&igrave; nghĩ m&igrave;nh chậm chạp trong qu&aacute; tr&igrave;nh tiếp thu b&agrave;i học.</li>\r\n</ul>\r\n<p>Khi tiếp x&uacute;c với những người th&agrave;nh c&ocirc;ng trong c&ocirc;ng việc, thường họ đ&atilde; c&oacute; vốn tiếng Anh rất tốt.&nbsp;Điều n&agrave;y đồng nghĩa bạn sẽ thấy được sự lạc quan, tự tin từ họ khi n&oacute;i về tiếng Anh :&nbsp;<em>&ldquo; tiếng Anh chỉ l&agrave; một trong h&agrave;ng ngh&igrave;n những thứ đơn giản bạn cần học &rdquo;</em>. L&uacute;c n&agrave;y bạn sẽ thấy được suy nghĩ t&iacute;ch cực của họ về việc học tiếng anh mạnh mẽ đến đ&acirc;u.</p>\r\n<p>Tr&ecirc;n l&yacute; thuyết&nbsp;l&agrave; thế nhưng bạn cũng n&ecirc;n tự biết rằng&nbsp;<strong>học tiếng Anh cho người mới bắt đầu</strong>&nbsp;cũng chẳng hề đơn giản bởi :</p>\r\n<ul>\r\n<li>Bạn kh&ocirc;ng thể mong chờ kết quả cấp tốc từ bất kỳ một lớp luyện, hay kh&oacute;a học ngắn n&agrave;o. N&oacute; l&agrave; cả một qu&aacute; tr&igrave;nh, bạn cần tiến bộ mỗi ng&agrave;y v&agrave; sử dụng thường xuy&ecirc;n th&igrave; mới ph&aacute;t huy được hiệu quả.</li>\r\n<li>Duy tr&igrave; được ngọn lửa quyết t&acirc;m trong cả qu&aacute; tr&igrave;nh học xuy&ecirc;n suốt.</li>\r\n<li>Phương ph&aacute;p v&agrave; th&aacute;i độ học tập lu&ocirc;n đi c&ugrave;ng nhau. C&oacute; th&aacute;i độ tốt nhưng kh&ocirc;ng đ&uacute;ng phương ph&aacute;p cũng chỉ l&atilde;ng ph&iacute; thời gian.</li>\r\n</ul>\r\n<p><em>Sau đ&acirc;y b&agrave;i viết&nbsp;sẽ chia sẻ cho c&aacute;c bạn biết được từ&nbsp;<strong>c&aacute;ch học tiếng Anh cho người mới bắt đầu</strong>&nbsp;đến phương ph&aacute;p, thời gian học ra sao. V&agrave; điều bạn cần l&agrave; sẵn s&agrave;ng h&agrave;nh động cho tương lai của m&igrave;nh.</em></p>\r\n<h2><strong>1. Nghe tiếng Anh thường xuy&ecirc;n</strong></h2>\r\n<p><img style=\"height: 469px; width: 640px;\" src=\"https://efc.edu.vn/wp-content/uploads/2017/05/hoc_tieng_anh_moi_ngay.png\" alt=\"hoc tieng anh moi ngay\" /></p>\r\n<p>Điều th&uacute; vị l&agrave; bạn c&oacute; thể học tiếng Anh bằng c&aacute;ch nghe d&ugrave; kh&ocirc;ng hiểu họ đang n&oacute;i g&igrave;. Nhiều nghi&ecirc;n cứu chỉ ra rằng mọi người c&oacute; khả năng học bất kỳ ng&ocirc;n ngữ n&agrave;o bằng c&aacute;ch lắng nghe. Đ&acirc;y l&agrave; c&aacute;ch học tự nhi&ecirc;n nhất. Minh chứng cụ thể l&agrave; trẻ em. Khi nhỏ, ch&uacute;ng chưa thể n&oacute;i m&agrave; chỉ biết lắng nghe. Ch&uacute;ng d&agrave;nh nhiều thời gian để nghe trước khi c&oacute; thể hiểu được những g&igrave; người đối diện n&oacute;i v&agrave; đạt được khả năng sử dụng ng&ocirc;n ngữ th&agrave;nh thạo.</p>\r\n<p>C&aacute;c nh&agrave; nghi&ecirc;n cứu sử dụng thuật ngữ đặc biệt để n&oacute;i về việc học ng&ocirc;n ngữ, đ&oacute; l&agrave; v&ocirc; thức hay tiềm ẩn. Việc học n&agrave;y diễn ra ngay cả khi bạn kh&ocirc;ng cần cố gắng hay ngồi v&agrave;o b&agrave;n học v&agrave; cũng chẳng cần phải thật tập trung. Bạn chỉ cần nghe thật nhiều tiếng Anh cũng c&oacute; thể học được. Bởi bộ n&atilde;o của bạn sẽ tự động hấp thụ &acirc;m thanh, giọng, lời n&oacute;i v&agrave; ngữ ph&aacute;p tiếng Anh, ngay cả khi bạn kh&ocirc;ng chủ t&acirc;m lắng nghe, n&oacute;i hoặc ghi ch&uacute;.</p>\r\n<p>V&igrave; vậy, điều trước nhất bạn cần l&agrave;m l&agrave; nghe tiếng Anh mọi l&uacute;c, mọi nơi, bất cứ khi n&agrave;o c&oacute; điều kiện qua TV, nghe nhạc, audiobook&hellip; Bạn cũng n&ecirc;n tới những nơi c&oacute; thể nghe được người bản ngữ n&oacute;i chuyện, giao tiếp với nhau. Tuy nhi&ecirc;n, bạn kh&ocirc;ng cần bắt m&igrave;nh ngồi một chỗ v&agrave; tập trung cao độ để lắng nghe. Thay v&agrave;o đ&oacute;, bạn c&oacute; thể vừa nghe tiếng Anh, vừa đi dạo, tham quan, nấu ăn, l&agrave;m việc nh&agrave; hay tập thể dục&hellip; miễn l&agrave; những &acirc;m thanh tiếng Anh vẫn c&oacute; thể &acirc;m vang trong tai của bạn.</p>\r\n<h2><strong>2. T&igrave;m hiểu c&aacute;c &acirc;m, c&aacute;ch ph&aacute;t &acirc;m trong tiếng Anh</strong></h2>\r\n<p>Bạn cần biết c&aacute;ch đọc c&aacute;c k&yacute; tự phi&ecirc;n &acirc;m quốc tế, nguy&ecirc;n &acirc;m (vowels) l&agrave; g&igrave;, phụ &acirc;m (consonants) l&agrave; g&igrave;. Khi bạn đọc đ&uacute;ng c&aacute;c &acirc;m,&nbsp;bạn c&oacute; thể nh&igrave;n v&agrave;o phi&ecirc;n &acirc;m trong từ điền v&agrave; đọc đ&uacute;ng bất kỳ từ vựng n&agrave;o.</p>\r\n<p>Những trang web hướng dẫn bạn c&aacute;ch đọc phi&ecirc;n &acirc;m quốc tế :</p>\r\n<ul>\r\n<li>http://cambridgeenglishonline.com</li>\r\n<li>http://www.antimoon.com</li>\r\n<li>http://www.learnersdictionary.com</li>\r\n</ul>\r\n<h2><strong>3. Li&ecirc;n kết từ mới với h&igrave;nh ảnh</strong></h2>\r\n<p><img style=\"height: 458px; width: 650px;\" src=\"https://efc.edu.vn/wp-content/uploads/2017/05/loi-ich-cua-hoc-tu-vung-tieng-anh-giao-tiep-bang-hinh-anh-1.jpg\" alt=\"hoc tieng anh qua hinh anh\" /></p>\r\n<p>&nbsp;</p>\r\n<p><strong>Học từ vựng tiếng anh kết hợp h&igrave;nh ảnh gi&uacute;p bạn nhớ l&acirc;u hơn&nbsp;:</strong>&nbsp;Việc kết hợp h&igrave;nh ảnh minh hoạ cho c&aacute;c từ vựng tiếng anh sẽ gi&uacute;p bạn nhớ từ mới dễ d&agrave;ng hơn th&ocirc;ng qua việc ghi nhớ bằng tranh ảnh minh họa. C&aacute;ch học n&agrave;y vừa giải tr&iacute; vừa tiếp thu kiến thức sẽ gi&uacute;p bạn cảm thấy hứng th&uacute; hơn v&agrave; nhớ từ l&acirc;u hơn so với c&aacute;ch học thuộc l&ograve;ng th&ocirc;ng thường.</p>\r\n<h2><strong>4. Học s&acirc;u</strong></h2>\r\n<p>Ngo&agrave;i việc nghe hằng ng&agrave;y việc học từ v&agrave; n&oacute;i cũng l&agrave; rất quan trọng. Tuy vậy d&ugrave; n&oacute;i thế n&agrave;o đi nữa bạn cũng phải học thật chậm, thật s&acirc;u để<a href=\"https://vi.wikipedia.org/wiki/Ti%E1%BA%BFng_Anh\">&nbsp;tiếng Anh</a>&nbsp;dần dần ngấm v&agrave;o đầu bạn. Việc học lướt qua, kh&ocirc;ng ch&uacute; t&acirc;m ghi nhớ sẽ chỉ l&agrave;m một ng&ocirc;n&nbsp; ngữ b&aacute;m l&ecirc;n người v&agrave; n&oacute; c&oacute; thể rời khỏi bạn bất kỳ l&uacute;c n&agrave;o. Với việc học từ h&atilde;y gh&eacute;p n&oacute; với c&aacute;c c&acirc;u, c&aacute;c văn bản rồi tạo cho m&igrave;nh 1 quyển sổ, nhật k&yacute; để ghi lại tất cả. Với học n&oacute;i h&atilde;y tập thường xuy&ecirc;n ở một chủ đề n&agrave;o đ&oacute; đến bao giờ tr&ocirc;i chảy mới th&ocirc;i. C&ograve;n học nghe như đ&atilde; n&oacute;i h&atilde;y thực hiện bất kỳ l&uacute;c n&agrave;o bạn c&oacute; thời gian. Học thật s&acirc;u bởi &ldquo;dục tốc bất đạt&rdquo; v&agrave; c&agrave;ng học kỹ bao nhi&ecirc;u th&igrave; c&agrave;ng nhanh v&agrave; hiệu quả bấy nhi&ecirc;u.</p>\r\n<h2><strong>5. Học tiếng anh qua phim ảnh</strong></h2>\r\n<p><img style=\"height: 400px; width: 650px;\" src=\"https://efc.edu.vn/wp-content/uploads/2017/05/Tu-hoc-tieng-anh-qua-phim-anh-3.jpg\" alt=\"hoc tieng anh qua phim anh\" /></p>\r\n<p>Đối với phương ph&aacute;p n&agrave;y bạn h&atilde;y chọn xem những phim c&oacute; phụ đề tiếng Anh. Khi xem phim, h&atilde;y ch&uacute; &yacute; đến lời thoại bạn kh&ocirc;ng hiểu nghĩa. Dừng phim v&agrave; so s&aacute;nh với phụ đề tiếng Việt (nếu c&oacute;) hoặc tra từ điển. Nếu sau n&agrave;y c&aacute;c bạn bắt gặp từ n&agrave;y ở đ&acirc;u đ&oacute;, bộ n&atilde;o sẽ khơi gợi lại t&igrave;nh huống đ&atilde; gặp trong phim v&agrave; bạn sẽ nhanh ch&oacute;ng nhớ ra nghĩa của từ. &nbsp;Đặc biệt với c&aacute;c bạn nhỏ tuổi sẽ rất th&iacute;ch th&uacute; khi vừa được ngắm ảnh vừa học những từ tiếng Anh th&uacute; vị qua từng chủ đề kh&aacute;c nhau vừa gần gũi vừa th&acirc;n thiết v&agrave; vừa nhận biết được thế giới xung quanh.</p>\r\n<h2><strong>6. D&ugrave;ng s&aacute;ch tham khảo ph&ugrave; hợp cho người mới bắt đầu</strong></h2>\r\n<p>C&oacute; kh&aacute; nhiều s&aacute;ch nhằm cung cấp cho từng y&ecirc;u cầu học tiếng Anh của mỗi người kh&aacute;c nhau. Bạn n&ecirc;n cẩn trọng kh&ocirc;n ngoan trong việc chọn s&aacute;ch th&iacute;ch hợp với m&igrave;nh. T&igrave;m đọc những s&aacute;ch của t&aacute;c giả n&agrave;o viết hay được nhiều người biết đến. N&ecirc;n biết m&igrave;nh mua thể loại n&agrave;o, cần cung ứng cho bạn điều g&igrave;. Đ&agrave;nh rằng cần nhiều s&aacute;ch để tham khảo nhưng kh&ocirc;ng phải bạ s&aacute;ch n&agrave;o dạy tiếng Anh l&agrave; mua, của bất cứ t&aacute;c giả n&agrave;o cũng kh&ocirc;ng cần chắt lọc vừa tốn tiền vửa kh&ocirc;ng hiệu quả.</p>\r\n<h2><strong>7. Kết hợp học nghe v&agrave; học ngữ ph&aacute;p</strong></h2>\r\n<p>Việc học nghe vừa bổ trợ cho bạn về ph&aacute;t &acirc;m, vừa gi&uacute;p bạn học ngữ ph&aacute;p, từ vựng. Ch&iacute;nh v&igrave; vậy khi bắt đầu học tiếng Anh, việc học nghe v&agrave; học ngữ ph&aacute;p thường đi đ&ocirc;i với nhau.</p>\r\n<p>Như đ&atilde; n&oacute;i ở tr&ecirc;n, bạn c&oacute; thể luyện tập nghe h&agrave;ng ng&agrave;y bằng c&aacute;c chương tr&igrave;nh, phim ảnh, ca kh&uacute;c tiếng Anh. Bạn h&atilde;y cố gắng nghe nhiều nhất c&oacute; thể. Nếu kh&ocirc;ng tập trung để học nghe được, h&atilde;y d&agrave;nh nghe một c&aacute;ch bị động. Khi bạn đợi xe bus, l&agrave;m việc nh&agrave;, trước khi đi ngủ, đi tắm&hellip; bạn h&atilde;y mở những file nghe tiếng Anh. C&oacute; thể nhiều chỗ kh&ocirc;ng hiểu nhưng tai bạn sẽ hấp thu ngoại ngữ một c&aacute;ch tự nhi&ecirc;n. Bạn cũng c&oacute; thể bắt đầu với gi&aacute;o tr&igrave;nh Listen carefully.</p>\r\n<p>Đối với những người bắt đầu học tiếng Anh, vốn từ chưa nhiều, bạn n&ecirc;n học cuốn&nbsp;<strong>English Grammar in use third Edition with Answers</strong>. Đ&acirc;y l&agrave; cuốn s&aacute;ch ngữ ph&aacute;p liền mạch, chứa đựng những vấn đề cơ bản v&agrave; dễ hiểu nhất. Mỗi b&agrave;i học đều c&oacute; v&iacute; dụ, h&igrave;nh ảnh, b&agrave;i tập, được sắp xếp rất khoa học.</p>\r\n<h2><strong>8. Luyện tập kĩ năng n&oacute;i</strong></h2>\r\n<p>Khi giao tiếp tiếng Anh, nghe v&agrave; n&oacute;i l&agrave; 2 kỹ năng vận dụng nhiều nhất trong giao tiếp. Bạn h&atilde;y lặp lại những g&igrave; m&igrave;nh được nghe, v&iacute; dụ : khi xem một bộ phim, nghe một bản tin&hellip; N&oacute;i chuyện với người bản ngữ l&agrave; một trong những c&aacute;ch luyện kĩ năng n&oacute;i hiệu quả nhất. Hoặc bạn cũng c&oacute; thể tham gia 1 CLB tiếng Anh để n&acirc;ng cao sự tự tin khi giao tiếp tiếng Anh của m&igrave;nh.</p>\r\n<h2><strong>9. Luyện tập kĩ năng đọc</strong></h2>\r\n<p>Đọc l&agrave; một kĩ năng cần thiết v&agrave; bạn c&oacute; thể học hỏi rất nhiều trong qu&aacute; tr&igrave;nh đọc. Khi đọc, bạn sẽ học được c&aacute;c cấu tr&uacute;c c&acirc;u mới, c&aacute;ch diễn đạt. Đồng thời, bạn sẽ c&oacute; vốn từ phong ph&uacute;. Bạn c&oacute; thể sử dụng những t&agrave;i liệu luyện đọc cho k&igrave; thi như Ielts, Toeic, Toefl,&hellip; những c&acirc;u chuyện thiếu nhi, mục quảng c&aacute;o, b&agrave;i b&aacute;o, b&agrave;i h&aacute;t,&hellip;</p>\r\n<h2><strong>10. Luyện tập kĩ năng viết</strong></h2>\r\n<p><img style=\"height: 426px; width: 650px;\" src=\"https://efc.edu.vn/wp-content/uploads/2017/05/writing-center-wordlie.jpg\" alt=\"tập viết tiếng anh\" /></p>\r\n<p>Cũng như ph&aacute;t &acirc;m hay nghe, đọc, điều cần l&agrave;m l&agrave; bạn phải thực h&agrave;nh thật nhiều. Với kĩ năng viết, ban đầu, bạn h&atilde;y tập viết những điều cơ bản. C&oacute; thể viết nhật k&iacute; bằng tiếng Anh, viết về 1 người bạn, 1 quyển s&aacute;ch hay&hellip; B&ecirc;n cạnh đ&oacute; h&atilde;y thu nạp th&ecirc;m nhiều từ mới. Đọc s&aacute;ch, truyện bằng tiếng Anh cũng gi&uacute;p bạn c&oacute; th&ecirc;m nhiều c&aacute;ch viết hay.</p>\r\n<p>Khi bắt đầu học tiếng Anh, bạn sẽ mất khoảng s&aacute;u th&aacute;ng để ho&agrave;n th&agrave;nh kiến thức nền tảng. Đ&acirc;y l&agrave; một khoảng thời gian v&ocirc; c&ugrave;ng quan trọng, ảnh hưởng trực tiếp đến khả năng tiếp thu kiến thức của bạn sau n&agrave;y. Sau khi đ&atilde; c&oacute; kiến thức tiếng Anh nền tảng, bạn h&atilde;y cố gắng vận dụng ch&uacute;ng thật nhiều trong cuộc sống. Bạn h&atilde;y đọc b&aacute;o, nghe nhạc, xem phim, nghe c&aacute;c bản tin, xem c&aacute;c chương tr&igrave;nh truyền h&igrave;nh,&hellip; v&agrave; giao tiếp với người bản ngữ.</p>\r\n<h3><strong>Lời Kết :</strong></h3>\r\n<p><em>Như vậy việc học tiếng Anh cho người mới bắt đầu chưa bao giờ đơn giản, nhưng nếu biết c&aacute;ch học v&agrave; đi đ&uacute;ng&nbsp;lịch tr&igrave;nh cụ thể th&igrave; kh&ocirc;ng c&ograve;n l&agrave; vấn đề bạn cần bận t&acirc;m. H&atilde;y đi đ&uacute;ng, c&oacute; phương ph&aacute;p đ&uacute;ng, v&agrave; quan trọng nhất l&agrave; lu&ocirc;n duy tr&igrave; năng lượng đ&oacute; trong suốt qu&aacute; tr&igrave;nh d&agrave;i học tiếng Anh.&nbsp;<strong>Học tiếng Anh cho người bắt đầu</strong>&nbsp;lu&ocirc;n cần c&oacute; kỹ thuật đi k&egrave;m v&agrave; bạn cần n&ecirc;n chuy&ecirc;n t&acirc;m chăm chỉ luyện tập. H&atilde;y &aacute;p dụng những kiến thức ở b&agrave;i viết tr&ecirc;n&nbsp;v&agrave; xem hiệu quả mang lại. Tr&acirc;n th&agrave;nh c&aacute;m ơn c&aacute;c bạn đ&atilde; quan t&acirc;m theo d&otilde;i.</em></p>', '2019-07-05', 7, 10, 0),
(4, 'Cách học từ vựng tiếng Anh thông minh', 'http://sic.edu.vn/upload/storage/tham-khao/4/boystudy.gif', 'Ghi nhớ từ vựng tiếng Anh là một phần vô cùng quan trọng trong việc học một ngôn ngữ mới, nhưng việc đó dường như lại khá khó để đạt được.', '<h2>1. Đọc</h2>\r\n<p>Đ&acirc;y l&agrave; hoạt động v&ocirc; c&ugrave;ng cần thiết v&igrave; đọc l&agrave; c&aacute;ch tốt nhất gi&uacute;p ch&uacute;ng ta tiếp cận với kho từ vựng phong ph&uacute;.</p>\r\n<p>N&ecirc;n nhớ rằng: bạn c&oacute; thể đọc bất cứ thứ g&igrave; m&agrave; bạn muốn! Bạn c&oacute; thể đọc s&aacute;ch văn học nếu đ&oacute; l&agrave; thể loại bạn ưa th&iacute;ch, nếu kh&ocirc;ng, h&atilde;y thử đọc truyện tranh hoặc tạp ch&iacute;. Bạn c&oacute; thể mua một quyển s&aacute;ch dạy nấu ăn để đọc, họặc đọc truyện cho lũ trẻ nh&agrave; bạn. C&oacute; h&agrave;ng ngh&igrave;n b&agrave;i b&aacute;o onlines, c&aacute;c website chứa nhiều th&ocirc;ng tin hữu &iacute;ch &hellip; sự lựa chọn cho bạn l&agrave; v&ocirc; hạn. V&igrave; vậy, h&atilde;y t&igrave;m đọc những thứ m&agrave; bạn cảm thấy hứng th&uacute;.</p>\r\n<p>Đọc l&agrave; một phương ph&aacute;p dễ thực hiện v&agrave; mang t&iacute;nh thư gi&atilde;n, n&oacute; sẽ gi&uacute;p bạn n&acirc;ng cao kỹ năng ng&ocirc;n ngữ của m&igrave;nh. Kh&ocirc;ng những thế, bạn sẽ học th&ecirc;m được nhiều điều mới. Một c&ocirc;ng đ&ocirc;i việc phải kh&ocirc;ng n&agrave;o.</p>\r\n<h2>2. Hiểu ngữ cảnh</h2>\r\n<p>Tuyệt vời! Vậy l&agrave; bạn đ&atilde; học được rất nhiều từ mới, nhưng nếu bạn kh&ocirc;ng hiểu phải sử dụng ch&uacute;ng như thế n&agrave;o v&agrave; khi n&agrave;o, th&igrave; bạn vẫn chưa ho&agrave;n to&agrave;n được coi l&agrave; đ&atilde; học được từ đ&oacute; đ&acirc;u.</p>\r\n<p>Đ&acirc;y l&agrave; l&yacute; do v&igrave; sao việc đọc v&ocirc; c&ugrave;ng quan trọng bởi khi đ&oacute; c&aacute;c từ được đặt trong một văn cảnh gi&uacute;p ch&uacute;ng ta hiểu được &yacute; nghĩa v&agrave; c&aacute;ch sử dụng của ch&uacute;ng.</p>\r\n<p>V&iacute; dụ: Trong c&acirc;u &ldquo;<strong>&nbsp;This soup is horrible, it tastes so bitter!</strong>&rdquo; Bạn c&oacute; thể kh&ocirc;ng biết nghĩa của từ &ldquo;bitter&rdquo;, nhưng nhờ được đặt trong cả c&acirc;u, bạn c&oacute; thể hiểu nghĩa của n&oacute; l&agrave; &ldquo;kh&ocirc;ng ngon&rdquo;.</p>\r\n<p>Ngữ cảnh gi&uacute;p ch&uacute;ng ta hiểu được một từ được sử dụng trong văn phong trang trọng hay đời thường, từ ngữ đ&oacute; c&oacute; thể sử dụng khi n&oacute;i chuyện với bạn b&egrave;, hay l&agrave; thường được d&ugrave;ng trong văn viết thay v&igrave; văn n&oacute;i.</p>\r\n<h2>3. Học c&aacute;c từ li&ecirc;n quan</h2>\r\n<p>Nếu như bạn đang học từ &ldquo;care&rdquo;, đừng dừng lại ở đ&oacute;. H&atilde;y sử dụng một quyển từ điển hoặc mạng Internet để t&igrave;m th&ecirc;m c&aacute;c từ ph&aacute;t sinh của từ đ&oacute;, v&agrave; c&aacute;c c&aacute;ch diễn đạt của từ đ&oacute;.</p>\r\n<p>V&iacute; dụ: bạn c&oacute; thể t&igrave;m thấy c&aacute;c từ li&ecirc;n quan như careful, carefree, careless, take care!</p>\r\n<p>Thấy kh&ocirc;ng n&agrave;o. Bạn bắt đầu với một từ, nhưng đ&atilde; nhanh ch&oacute;ng học th&ecirc;m được 4 từ nữa. V&agrave; bởi v&igrave; nghĩa của c&aacute;c từ c&oacute; li&ecirc;n quan đến nhau, n&ecirc;n sẽ dễ d&agrave;ng hơn để hiểu v&agrave; nhớ mỗi từ.</p>\r\n<h2>4. Đặt c&acirc;u</h2>\r\n<p>Đặt c&acirc;u l&agrave; c&aacute;ch gi&uacute;p ch&uacute;ng ta biến những thứ ch&uacute;ng ta đ&atilde; học th&agrave;nh h&agrave;nh động. Bởi v&igrave; tuy bạn đ&atilde; học v&agrave; hiểu được c&aacute;ch sử dụng một từ mới, nhưng để cho n&atilde;o bộ c&oacute; thể ghi nhớ được từ đ&oacute; l&acirc;u d&agrave;i trong tương lai th&igrave; c&aacute;ch tốt nhất l&agrave; sử dụng ngay.</p>\r\n<p>H&atilde;y đặt 10 c&acirc;u, sử dụng c&aacute;c nghĩa kh&aacute;c nhau của từ m&agrave; bạn muốn học, hoặc nếu như đ&oacute; l&agrave; một động từ, bạn c&oacute; thể d&ugrave;ng c&aacute;c th&igrave; kh&aacute;c nhau.</p>\r\n<p>V&iacute; dụ: nếu bạn muốn nhớ cụm động từ &ldquo;<strong>to tidy up</strong>&rdquo;, h&atilde;y đặt một v&agrave;i c&acirc;u như:</p>\r\n<p>&ldquo;<strong>Maria, you must tidy your room up</strong>&rdquo;, &ldquo;<strong>I have to tidy up before my friends come</strong>&rdquo;, &ldquo;<strong>Paul will watch TV after he finishes tidying up the kitchen</strong>&rdquo;.</p>\r\n<p>Bạn đ&atilde; bao giờ nghe c&acirc;u n&oacute;i n&agrave;y của Benjamin Franklin&rsquo;s chưa: &ldquo;<strong>Tell me and I forget, teach me and I may remember,&nbsp;involve me and I learn</strong>&rdquo;?</p>\r\n<p>Tạm dịch l&agrave;: &ldquo;N&oacute;i với t&ocirc;i v&agrave; t&ocirc;i sẽ qu&ecirc;n. Dạy t&ocirc;i v&agrave; t&ocirc;i sẽ nhớ. Cho t&ocirc;i l&agrave;m v&agrave; t&ocirc;i sẽ học&rdquo;.</p>\r\n<p>Do vậy, bằng c&aacute;ch tự đặt c&acirc;u, bạn đang y&ecirc;u cầu n&atilde;o phải bộ hoạt động v&agrave; trực tiếp tham gia v&agrave;o qu&aacute; tr&igrave;nh học.</p>\r\n<h2>5. Ghi &acirc;m</h2>\r\n<p>Bằng c&aacute;ch nghe lại giọng của ch&iacute;nh m&igrave;nh khi đọc to một từ v&agrave; h&igrave;nh dung ra sự cử động của miệng khi ph&aacute;t &acirc;m, bạn đang tạo ra sự kết nối với n&atilde;o bộ.</p>\r\n<p>Nếu bạn đ&atilde; từng học tiếng Anh trong qu&aacute; khứ nhưng kh&ocirc;ng sử dụng trong một thời gian d&agrave;i, bạn c&oacute; thể biết c&aacute;c từ nhưng sẽ cảm thấy rất kh&oacute; để nhớ ch&uacute;ng. V&igrave; vậy, khi bạn n&oacute;i, n&oacute; khiến cho việc nhớ ra c&aacute;c từ dễ d&agrave;ng hơn.</p>\r\n<p>H&atilde;y d&ugrave;ng một chiếc camera, điện thoại th&ocirc;ng minh hoặc webcam để ghi lại việc bạn luyện tập từ vựng v&agrave; đặt c&acirc;u.</p>\r\n<h2>6. L&agrave;m flashcards</h2>\r\n<p>Flashcards l&agrave; c&aacute;ch phổ biến để học từ mới. Bạn c&oacute; thể d&ugrave;ng c&aacute;c tờ giấy nhớ v&agrave; d&aacute;n ch&uacute;ng ở những nơi m&agrave; bạn thường xuy&ecirc;n nh&igrave;n, hoặc vẽ một h&igrave;nh đơn giản m&ocirc; tả định nghĩa của từ nếu bạn thuộc tu&yacute;p học bằng trực quan.</p>\r\n<p>H&agrave;ng ng&agrave;y, h&atilde;y l&ocirc;i những chiếc flashcard của bạn ra v&agrave; đọc ch&uacute;ng.</p>\r\n<h2>7. Ghi ch&uacute;</h2>\r\n<p>Một b&iacute; quyết nữa l&agrave; lu&ocirc;n mang theo b&ecirc;n m&igrave;nh một cuốn sổ v&agrave; b&uacute;t. Khi bạn nghe thấy một từ hoặc một c&acirc;u trong một bộ phim hoặc một b&agrave;i h&aacute;t m&agrave; bạn cảm thấy th&iacute;ch, bạn c&oacute; thể viết n&oacute; v&agrave;o sổ. Khi trở về nh&agrave;, bạn c&oacute; thể tra cứu về từ đ&oacute; v&agrave; th&ecirc;m n&oacute; v&agrave;o trong danh s&aacute;ch c&aacute;c từ m&agrave; bạn đ&atilde; học.</p>\r\n<h2>8. Chơi tr&ograve; chơi</h2>\r\n<p>Khi bạn cảm thấy vui vẻ bạn sẽ học một c&aacute;ch dễ d&agrave;ng hơn. V&igrave; vậy, chơi một số tr&ograve; chơi như xếp chữ, giải &ocirc; chữ sẽ gi&uacute;p kiểm tra khả năng s&aacute;ng tạo của bạn v&agrave; k&iacute;ch th&iacute;ch c&aacute;c suy nghĩ vượt ra ngo&agrave;i khu&ocirc;n khổ.</p>\r\n<h2>9. Luyện n&oacute;i</h2>\r\n<p>Ở b&iacute; quyết số 5, ch&uacute;ng t&ocirc;i đ&atilde; giải th&iacute;ch v&igrave; sao việc ghi &acirc;m lại giọng n&oacute;i của bản th&acirc;n gi&uacute;p bạn nhớ từ tốt hơn.</p>\r\n<p>Nếu bạn kh&ocirc;ng c&oacute; bất kỳ người bạn nước ngo&agrave;i n&agrave;o (người bản xứ nh&eacute;), đừng lo lắng, h&atilde;y mời những người bạn cũng c&oacute; nhu cầu học tiếng Anh đi caf&eacute; v&agrave; c&ugrave;ng nhau luyện tập.</p>\r\n<p>Để đạt được hiệu quả tốt nhất, bạn n&ecirc;n lựa chọn mỗi tuần một chủ đề kh&aacute;c nhau để c&ugrave;ng trao đổi. C&aacute;ch n&agrave;y sẽ gi&uacute;p cho bạn luyện tập sử dụng c&aacute;c từ vựng kh&aacute;c nhau, qua đ&oacute; l&agrave;m phong ph&uacute; th&ecirc;m vốn từ của bạn. Nhớ d&agrave;nh ra khoảng 1 &ndash; 2 giờ để lập danh s&aacute;ch c&aacute;c từ m&agrave; bạn muốn sử dụng trước khi tham gia v&agrave;o cuộc thảo luận với bạn b&egrave; nh&eacute;.</p>\r\n<h2>10. Lặp đi lặp lại</h2>\r\n<p>Người T&acirc;y Ban Nha c&oacute; c&acirc;u: &ldquo;la repetici&oacute;n es la madre del &eacute;xito&rdquo;, nghĩa của n&oacute; l&agrave; &ldquo;Sự r&egrave;n luyện (lặp đi lặp lại) l&agrave; ch&igrave;a kh&oacute;a của th&agrave;nh c&ocirc;ng&rdquo;. Điều n&agrave;y ho&agrave;n to&agrave;n đ&uacute;ng. Để học bất kỳ điều g&igrave;, bạn cần phải luyện tập, luyện tập, luyện tập.</p>\r\n<p>Mỗi ng&agrave;y, h&atilde;y đặt ra một khoảng thời gian để học từ vựng. Việc bạn l&agrave;m như thế n&agrave;o kh&ocirc;ng quan trọng, vấn đề l&agrave; bạn cần luyện tập mỗi ng&agrave;y, &iacute;t một, nhưng đều đặn. Điều n&agrave;y sẽ gi&uacute;p tạo ra th&oacute;i quen tốt cho bản th&acirc;n bạn.</p>\r\n<h2>11. Ki&ecirc;n nhẫn</h2>\r\n<p>V&agrave; cuối c&ugrave;ng: h&atilde;y ki&ecirc;n nhẫn với bản th&acirc;n.</p>\r\n<p>Học được tiếng Anh l&agrave; một th&agrave;nh quả lớn, v&agrave; bạn n&ecirc;n cảm thấy tự h&agrave;o về bản th&acirc;n mỗi khi bạn học được th&ecirc;m 1 từ mới.</p>\r\n<p>Vậy l&agrave; bạn đ&atilde; c&oacute; trong tay&nbsp;<a href=\"http://apollo360.edu.vn/tieng-anh-doanh-nghiep/11-bi-quyet-tu-hoc-tu-vung-de-nho-hieu-qua/\">11 b&iacute; quyết học từ vựng</a>&nbsp;v&ocirc; c&ugrave;ng hiệu quả rồi đ&oacute;. Đừng qu&ecirc;n chia sẻ với ch&uacute;ng t&ocirc;i b&iacute; quyết của bạn bằng c&aacute;ch b&igrave;nh luận ở b&ecirc;n dưới nh&eacute;.</p>\r\n<p>Ch&uacute;c c&aacute;c bạn th&agrave;nh c&ocirc;ng!</p>', '2019-06-01', 7, 10, 0),
(7, 'Cẩm Nang Cho Người Mới Bắt Đầu', 'http://static1.squarespace.com/static/5922f26a8419c22a1e72d402/5b51f4e96d2a735aec402ee7/5be08281032be49b18b8ac98/1548954754826/IMAGINE.jpg?format=1500w', 'Bạn là 1 người mới làm quen với tiếng Anh? Đừng quá lo lắng hãy đọc bài viết này để xác định cho mình 1 con đường học tiếng Anh phù hợp nhé.', '<p>C&aacute;c bạn đang trong thắc mắc về c&aacute;ch<a href=\"https://jaxtina.com/khoa-hoc/khoa-hoc-tieng-anh-cho-nguoi-moi-bat-dau/\"><strong>&nbsp;học tiếng anh cho người mới bắt đầu</strong></a><strong>&nbsp;</strong>hoặc mất gốc sau thời gian d&agrave;i kh&ocirc;ng tiếp x&uacute;c, v&agrave; bạn đang suy nghĩ trong đầu những băn khoăn :</p>\r\n<ul>\r\n<li>Tự ti v&igrave; mất hết lượng kiến thức căn bản</li>\r\n<li>Lo lắng v&igrave; m&igrave;nh bắt đầu h&agrave;nh tr&igrave;nh học tiếng Anh chậm hơn người kh&aacute;c</li>\r\n<li>Nghĩ rằng tiếng Anh kh&oacute; học, m&igrave;nh sẽ rất kh&oacute; khăn để theo kịp người ta</li>\r\n<li>Sợ h&atilde;i v&igrave; nghĩ m&igrave;nh chậm chạp trong qu&aacute; tr&igrave;nh tiếp thu b&agrave;i học.</li>\r\n</ul>\r\n<p>Khi tiếp x&uacute;c với những người th&agrave;nh c&ocirc;ng trong c&ocirc;ng việc, thường họ đ&atilde; c&oacute; vốn tiếng Anh rất tốt.&nbsp;Điều n&agrave;y đồng nghĩa bạn sẽ thấy được sự lạc quan, tự tin từ họ khi n&oacute;i về tiếng Anh :&nbsp;<em>&ldquo; tiếng Anh chỉ l&agrave; một trong h&agrave;ng ngh&igrave;n những thứ đơn giản bạn cần học &rdquo;</em>. L&uacute;c n&agrave;y bạn sẽ thấy được suy nghĩ t&iacute;ch cực của họ về việc học tiếng anh mạnh mẽ đến đ&acirc;u.</p>\r\n<p>Tr&ecirc;n l&yacute; thuyết&nbsp;l&agrave; thế nhưng bạn cũng n&ecirc;n tự biết rằng&nbsp;<strong>học tiếng Anh cho người mới bắt đầu</strong>&nbsp;cũng chẳng hề đơn giản bởi :</p>\r\n<ul>\r\n<li>Bạn kh&ocirc;ng thể mong chờ kết quả cấp tốc từ bất kỳ một lớp luyện, hay kh&oacute;a học ngắn n&agrave;o. N&oacute; l&agrave; cả một qu&aacute; tr&igrave;nh, bạn cần tiến bộ mỗi ng&agrave;y v&agrave; sử dụng thường xuy&ecirc;n th&igrave; mới ph&aacute;t huy được hiệu quả.</li>\r\n<li>Duy tr&igrave; được ngọn lửa quyết t&acirc;m trong cả qu&aacute; tr&igrave;nh học xuy&ecirc;n suốt.</li>\r\n<li>Phương ph&aacute;p v&agrave; th&aacute;i độ học tập lu&ocirc;n đi c&ugrave;ng nhau. C&oacute; th&aacute;i độ tốt nhưng kh&ocirc;ng đ&uacute;ng phương ph&aacute;p cũng chỉ l&atilde;ng ph&iacute; thời gian.</li>\r\n</ul>\r\n<p><em>Sau đ&acirc;y b&agrave;i viết&nbsp;sẽ chia sẻ cho c&aacute;c bạn biết được từ&nbsp;<strong>c&aacute;ch học tiếng Anh cho người mới bắt đầu</strong>&nbsp;đến phương ph&aacute;p, thời gian học ra sao. V&agrave; điều bạn cần l&agrave; sẵn s&agrave;ng h&agrave;nh động cho tương lai của m&igrave;nh.</em></p>\r\n<h2><strong>1. Nghe tiếng Anh thường xuy&ecirc;n</strong></h2>\r\n<p><img style=\"height: 469px; width: 640px;\" src=\"https://efc.edu.vn/wp-content/uploads/2017/05/hoc_tieng_anh_moi_ngay.png\" alt=\"hoc tieng anh moi ngay\" /></p>\r\n<p>Điều th&uacute; vị l&agrave; bạn c&oacute; thể học tiếng Anh bằng c&aacute;ch nghe d&ugrave; kh&ocirc;ng hiểu họ đang n&oacute;i g&igrave;. Nhiều nghi&ecirc;n cứu chỉ ra rằng mọi người c&oacute; khả năng học bất kỳ ng&ocirc;n ngữ n&agrave;o bằng c&aacute;ch lắng nghe. Đ&acirc;y l&agrave; c&aacute;ch học tự nhi&ecirc;n nhất. Minh chứng cụ thể l&agrave; trẻ em. Khi nhỏ, ch&uacute;ng chưa thể n&oacute;i m&agrave; chỉ biết lắng nghe. Ch&uacute;ng d&agrave;nh nhiều thời gian để nghe trước khi c&oacute; thể hiểu được những g&igrave; người đối diện n&oacute;i v&agrave; đạt được khả năng sử dụng ng&ocirc;n ngữ th&agrave;nh thạo.</p>\r\n<p>C&aacute;c nh&agrave; nghi&ecirc;n cứu sử dụng thuật ngữ đặc biệt để n&oacute;i về việc học ng&ocirc;n ngữ, đ&oacute; l&agrave; v&ocirc; thức hay tiềm ẩn. Việc học n&agrave;y diễn ra ngay cả khi bạn kh&ocirc;ng cần cố gắng hay ngồi v&agrave;o b&agrave;n học v&agrave; cũng chẳng cần phải thật tập trung. Bạn chỉ cần nghe thật nhiều tiếng Anh cũng c&oacute; thể học được. Bởi bộ n&atilde;o của bạn sẽ tự động hấp thụ &acirc;m thanh, giọng, lời n&oacute;i v&agrave; ngữ ph&aacute;p tiếng Anh, ngay cả khi bạn kh&ocirc;ng chủ t&acirc;m lắng nghe, n&oacute;i hoặc ghi ch&uacute;.</p>\r\n<p>V&igrave; vậy, điều trước nhất bạn cần l&agrave;m l&agrave; nghe tiếng Anh mọi l&uacute;c, mọi nơi, bất cứ khi n&agrave;o c&oacute; điều kiện qua TV, nghe nhạc, audiobook&hellip; Bạn cũng n&ecirc;n tới những nơi c&oacute; thể nghe được người bản ngữ n&oacute;i chuyện, giao tiếp với nhau. Tuy nhi&ecirc;n, bạn kh&ocirc;ng cần bắt m&igrave;nh ngồi một chỗ v&agrave; tập trung cao độ để lắng nghe. Thay v&agrave;o đ&oacute;, bạn c&oacute; thể vừa nghe tiếng Anh, vừa đi dạo, tham quan, nấu ăn, l&agrave;m việc nh&agrave; hay tập thể dục&hellip; miễn l&agrave; những &acirc;m thanh tiếng Anh vẫn c&oacute; thể &acirc;m vang trong tai của bạn.</p>\r\n<h2><strong>2. T&igrave;m hiểu c&aacute;c &acirc;m, c&aacute;ch ph&aacute;t &acirc;m trong tiếng Anh</strong></h2>\r\n<p>Bạn cần biết c&aacute;ch đọc c&aacute;c k&yacute; tự phi&ecirc;n &acirc;m quốc tế, nguy&ecirc;n &acirc;m (vowels) l&agrave; g&igrave;, phụ &acirc;m (consonants) l&agrave; g&igrave;. Khi bạn đọc đ&uacute;ng c&aacute;c &acirc;m,&nbsp;bạn c&oacute; thể nh&igrave;n v&agrave;o phi&ecirc;n &acirc;m trong từ điền v&agrave; đọc đ&uacute;ng bất kỳ từ vựng n&agrave;o.</p>\r\n<p>Những trang web hướng dẫn bạn c&aacute;ch đọc phi&ecirc;n &acirc;m quốc tế :</p>\r\n<ul>\r\n<li>http://cambridgeenglishonline.com</li>\r\n<li>http://www.antimoon.com</li>\r\n<li>http://www.learnersdictionary.com</li>\r\n</ul>\r\n<h2><strong>3. Li&ecirc;n kết từ mới với h&igrave;nh ảnh</strong></h2>\r\n<p><img style=\"height: 458px; width: 650px;\" src=\"https://efc.edu.vn/wp-content/uploads/2017/05/loi-ich-cua-hoc-tu-vung-tieng-anh-giao-tiep-bang-hinh-anh-1.jpg\" alt=\"hoc tieng anh qua hinh anh\" /></p>\r\n<p>&nbsp;</p>\r\n<p><strong>Học từ vựng tiếng anh kết hợp h&igrave;nh ảnh gi&uacute;p bạn nhớ l&acirc;u hơn&nbsp;:</strong>&nbsp;Việc kết hợp h&igrave;nh ảnh minh hoạ cho c&aacute;c từ vựng tiếng anh sẽ gi&uacute;p bạn nhớ từ mới dễ d&agrave;ng hơn th&ocirc;ng qua việc ghi nhớ bằng tranh ảnh minh họa. C&aacute;ch học n&agrave;y vừa giải tr&iacute; vừa tiếp thu kiến thức sẽ gi&uacute;p bạn cảm thấy hứng th&uacute; hơn v&agrave; nhớ từ l&acirc;u hơn so với c&aacute;ch học thuộc l&ograve;ng th&ocirc;ng thường.</p>\r\n<h2><strong>4. Học s&acirc;u</strong></h2>\r\n<p>Ngo&agrave;i việc nghe hằng ng&agrave;y việc học từ v&agrave; n&oacute;i cũng l&agrave; rất quan trọng. Tuy vậy d&ugrave; n&oacute;i thế n&agrave;o đi nữa bạn cũng phải học thật chậm, thật s&acirc;u để<a href=\"https://vi.wikipedia.org/wiki/Ti%E1%BA%BFng_Anh\">&nbsp;tiếng Anh</a>&nbsp;dần dần ngấm v&agrave;o đầu bạn. Việc học lướt qua, kh&ocirc;ng ch&uacute; t&acirc;m ghi nhớ sẽ chỉ l&agrave;m một ng&ocirc;n&nbsp; ngữ b&aacute;m l&ecirc;n người v&agrave; n&oacute; c&oacute; thể rời khỏi bạn bất kỳ l&uacute;c n&agrave;o. Với việc học từ h&atilde;y gh&eacute;p n&oacute; với c&aacute;c c&acirc;u, c&aacute;c văn bản rồi tạo cho m&igrave;nh 1 quyển sổ, nhật k&yacute; để ghi lại tất cả. Với học n&oacute;i h&atilde;y tập thường xuy&ecirc;n ở một chủ đề n&agrave;o đ&oacute; đến bao giờ tr&ocirc;i chảy mới th&ocirc;i. C&ograve;n học nghe như đ&atilde; n&oacute;i h&atilde;y thực hiện bất kỳ l&uacute;c n&agrave;o bạn c&oacute; thời gian. Học thật s&acirc;u bởi &ldquo;dục tốc bất đạt&rdquo; v&agrave; c&agrave;ng học kỹ bao nhi&ecirc;u th&igrave; c&agrave;ng nhanh v&agrave; hiệu quả bấy nhi&ecirc;u.</p>\r\n<h2><strong>5. Học tiếng anh qua phim ảnh</strong></h2>\r\n<p><img style=\"height: 400px; width: 650px;\" src=\"https://efc.edu.vn/wp-content/uploads/2017/05/Tu-hoc-tieng-anh-qua-phim-anh-3.jpg\" alt=\"hoc tieng anh qua phim anh\" /></p>\r\n<p>Đối với phương ph&aacute;p n&agrave;y bạn h&atilde;y chọn xem những phim c&oacute; phụ đề tiếng Anh. Khi xem phim, h&atilde;y ch&uacute; &yacute; đến lời thoại bạn kh&ocirc;ng hiểu nghĩa. Dừng phim v&agrave; so s&aacute;nh với phụ đề tiếng Việt (nếu c&oacute;) hoặc tra từ điển. Nếu sau n&agrave;y c&aacute;c bạn bắt gặp từ n&agrave;y ở đ&acirc;u đ&oacute;, bộ n&atilde;o sẽ khơi gợi lại t&igrave;nh huống đ&atilde; gặp trong phim v&agrave; bạn sẽ nhanh ch&oacute;ng nhớ ra nghĩa của từ. &nbsp;Đặc biệt với c&aacute;c bạn nhỏ tuổi sẽ rất th&iacute;ch th&uacute; khi vừa được ngắm ảnh vừa học những từ tiếng Anh th&uacute; vị qua từng chủ đề kh&aacute;c nhau vừa gần gũi vừa th&acirc;n thiết v&agrave; vừa nhận biết được thế giới xung quanh.</p>\r\n<h2><strong>6. D&ugrave;ng s&aacute;ch tham khảo ph&ugrave; hợp cho người mới bắt đầu</strong></h2>\r\n<p>C&oacute; kh&aacute; nhiều s&aacute;ch nhằm cung cấp cho từng y&ecirc;u cầu học tiếng Anh của mỗi người kh&aacute;c nhau. Bạn n&ecirc;n cẩn trọng kh&ocirc;n ngoan trong việc chọn s&aacute;ch th&iacute;ch hợp với m&igrave;nh. T&igrave;m đọc những s&aacute;ch của t&aacute;c giả n&agrave;o viết hay được nhiều người biết đến. N&ecirc;n biết m&igrave;nh mua thể loại n&agrave;o, cần cung ứng cho bạn điều g&igrave;. Đ&agrave;nh rằng cần nhiều s&aacute;ch để tham khảo nhưng kh&ocirc;ng phải bạ s&aacute;ch n&agrave;o dạy tiếng Anh l&agrave; mua, của bất cứ t&aacute;c giả n&agrave;o cũng kh&ocirc;ng cần chắt lọc vừa tốn tiền vửa kh&ocirc;ng hiệu quả.</p>\r\n<h2><strong>7. Kết hợp học nghe v&agrave; học ngữ ph&aacute;p</strong></h2>\r\n<p>Việc học nghe vừa bổ trợ cho bạn về ph&aacute;t &acirc;m, vừa gi&uacute;p bạn học ngữ ph&aacute;p, từ vựng. Ch&iacute;nh v&igrave; vậy khi bắt đầu học tiếng Anh, việc học nghe v&agrave; học ngữ ph&aacute;p thường đi đ&ocirc;i với nhau.</p>\r\n<p>Như đ&atilde; n&oacute;i ở tr&ecirc;n, bạn c&oacute; thể luyện tập nghe h&agrave;ng ng&agrave;y bằng c&aacute;c chương tr&igrave;nh, phim ảnh, ca kh&uacute;c tiếng Anh. Bạn h&atilde;y cố gắng nghe nhiều nhất c&oacute; thể. Nếu kh&ocirc;ng tập trung để học nghe được, h&atilde;y d&agrave;nh nghe một c&aacute;ch bị động. Khi bạn đợi xe bus, l&agrave;m việc nh&agrave;, trước khi đi ngủ, đi tắm&hellip; bạn h&atilde;y mở những file nghe tiếng Anh. C&oacute; thể nhiều chỗ kh&ocirc;ng hiểu nhưng tai bạn sẽ hấp thu ngoại ngữ một c&aacute;ch tự nhi&ecirc;n. Bạn cũng c&oacute; thể bắt đầu với gi&aacute;o tr&igrave;nh Listen carefully.</p>\r\n<p>Đối với những người bắt đầu học tiếng Anh, vốn từ chưa nhiều, bạn n&ecirc;n học cuốn&nbsp;<strong>English Grammar in use third Edition with Answers</strong>. Đ&acirc;y l&agrave; cuốn s&aacute;ch ngữ ph&aacute;p liền mạch, chứa đựng những vấn đề cơ bản v&agrave; dễ hiểu nhất. Mỗi b&agrave;i học đều c&oacute; v&iacute; dụ, h&igrave;nh ảnh, b&agrave;i tập, được sắp xếp rất khoa học.</p>\r\n<h2><strong>8. Luyện tập kĩ năng n&oacute;i</strong></h2>\r\n<p>Khi giao tiếp tiếng Anh, nghe v&agrave; n&oacute;i l&agrave; 2 kỹ năng vận dụng nhiều nhất trong giao tiếp. Bạn h&atilde;y lặp lại những g&igrave; m&igrave;nh được nghe, v&iacute; dụ : khi xem một bộ phim, nghe một bản tin&hellip; N&oacute;i chuyện với người bản ngữ l&agrave; một trong những c&aacute;ch luyện kĩ năng n&oacute;i hiệu quả nhất. Hoặc bạn cũng c&oacute; thể tham gia 1 CLB tiếng Anh để n&acirc;ng cao sự tự tin khi giao tiếp tiếng Anh của m&igrave;nh.</p>\r\n<h2><strong>9. Luyện tập kĩ năng đọc</strong></h2>\r\n<p>Đọc l&agrave; một kĩ năng cần thiết v&agrave; bạn c&oacute; thể học hỏi rất nhiều trong qu&aacute; tr&igrave;nh đọc. Khi đọc, bạn sẽ học được c&aacute;c cấu tr&uacute;c c&acirc;u mới, c&aacute;ch diễn đạt. Đồng thời, bạn sẽ c&oacute; vốn từ phong ph&uacute;. Bạn c&oacute; thể sử dụng những t&agrave;i liệu luyện đọc cho k&igrave; thi như Ielts, Toeic, Toefl,&hellip; những c&acirc;u chuyện thiếu nhi, mục quảng c&aacute;o, b&agrave;i b&aacute;o, b&agrave;i h&aacute;t,&hellip;</p>\r\n<h2><strong>10. Luyện tập kĩ năng viết</strong></h2>\r\n<p><img style=\"height: 426px; width: 650px;\" src=\"https://efc.edu.vn/wp-content/uploads/2017/05/writing-center-wordlie.jpg\" alt=\"tập viết tiếng anh\" /></p>\r\n<p>Cũng như ph&aacute;t &acirc;m hay nghe, đọc, điều cần l&agrave;m l&agrave; bạn phải thực h&agrave;nh thật nhiều. Với kĩ năng viết, ban đầu, bạn h&atilde;y tập viết những điều cơ bản. C&oacute; thể viết nhật k&iacute; bằng tiếng Anh, viết về 1 người bạn, 1 quyển s&aacute;ch hay&hellip; B&ecirc;n cạnh đ&oacute; h&atilde;y thu nạp th&ecirc;m nhiều từ mới. Đọc s&aacute;ch, truyện bằng tiếng Anh cũng gi&uacute;p bạn c&oacute; th&ecirc;m nhiều c&aacute;ch viết hay.</p>\r\n<p>Khi bắt đầu học tiếng Anh, bạn sẽ mất khoảng s&aacute;u th&aacute;ng để ho&agrave;n th&agrave;nh kiến thức nền tảng. Đ&acirc;y l&agrave; một khoảng thời gian v&ocirc; c&ugrave;ng quan trọng, ảnh hưởng trực tiếp đến khả năng tiếp thu kiến thức của bạn sau n&agrave;y. Sau khi đ&atilde; c&oacute; kiến thức tiếng Anh nền tảng, bạn h&atilde;y cố gắng vận dụng ch&uacute;ng thật nhiều trong cuộc sống. Bạn h&atilde;y đọc b&aacute;o, nghe nhạc, xem phim, nghe c&aacute;c bản tin, xem c&aacute;c chương tr&igrave;nh truyền h&igrave;nh,&hellip; v&agrave; giao tiếp với người bản ngữ.</p>\r\n<h3><strong>Lời Kết :</strong></h3>\r\n<p><em>Như vậy việc học tiếng Anh cho người mới bắt đầu chưa bao giờ đơn giản, nhưng nếu biết c&aacute;ch học v&agrave; đi đ&uacute;ng&nbsp;lịch tr&igrave;nh cụ thể th&igrave; kh&ocirc;ng c&ograve;n l&agrave; vấn đề bạn cần bận t&acirc;m. H&atilde;y đi đ&uacute;ng, c&oacute; phương ph&aacute;p đ&uacute;ng, v&agrave; quan trọng nhất l&agrave; lu&ocirc;n duy tr&igrave; năng lượng đ&oacute; trong suốt qu&aacute; tr&igrave;nh d&agrave;i học tiếng Anh.&nbsp;<strong>Học tiếng Anh cho người bắt đầu</strong>&nbsp;lu&ocirc;n cần c&oacute; kỹ thuật đi k&egrave;m v&agrave; bạn cần n&ecirc;n chuy&ecirc;n t&acirc;m chăm chỉ luyện tập. H&atilde;y &aacute;p dụng những kiến thức ở b&agrave;i viết tr&ecirc;n&nbsp;v&agrave; xem hiệu quả mang lại. Tr&acirc;n th&agrave;nh c&aacute;m ơn c&aacute;c bạn đ&atilde; quan t&acirc;m theo d&otilde;i.</em></p>', '2019-07-05', 7, 10, 0);

